$("#contactForm").validator().on("submit", function (event) {
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        submitMSG(false, "Avez vous bien rempli les champs?");
    } else {
        // everything looks good!
        sendEmail()
        formSuccess()
        event.preventDefault();
    }
});


    // Initiate Variables With Form Content
    function sendEmail() {
        let email = document.getElementById("email").value.trim()
        let message = document.getElementById("message").value.trim()
        let msg_subject = document.getElementById('msg_subject').value
        let name = document.getElementById("name").value
        if(email != '' && message != '' && name != '' && msg_subject != ''){
            var templateParams = {
          from_name: email,
          to_name: EMAIL,
          message: message
        };
      
        emailjs.send('service_orgpheg', 'template_s41n6zf', templateParams)
          .then(function(response) {
            document.getElementById("email").value = ''
            document.getElementById("message").value = ''
            document.getElementById("name").value = ''
            document.getElementById("msg_subject").value = ''
          }, function(error) {
            console.error('Erreur lors de l\'envoi de l\'e-mail :', error);
          });
        }
        
      }


function formSuccess(){
    $("#contactForm")[0].reset();
    submitMSG(true, "E-mail envoyé avec succès !")
}

function formError(){
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function(){
        $(this).removeClass();
    });
}

function submitMSG(valid, msg){
    if(valid){
        var msgClasses = "h3 text-left tada animated text-success";
    } else {
        var msgClasses = "h3 text-left text-danger";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}


